import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [network, setNetwork] = useState("192.168.1.0/24");
  const [profile, setProfile] = useState("fast");
  const [scanId, setScanId] = useState(null);
  const [status, setStatus] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const startScan = async () => {
    setLoading(true);
    setResult(null);
    try {
      const res = await axios.post("/api/scan/start", { network, profile });
      setScanId(res.data.scan_id);
      setStatus("started");
    } catch (err) {
      alert(err.response?.data?.error || "Failed to start scan");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
  if (!scanId) return;

  const interval = setInterval(async () => {
    try {
      const res = await axios.get(`/api/scan/status/${scanId}`);
      setStatus(res.data.status);

      if (res.data.status === "finished") {
        clearInterval(interval);

        const resultRes = await axios.get(`/api/scan/result/${scanId}`);
        // Handle both backend response shapes gracefully
        const data = resultRes.data.results || resultRes.data.devices || [];
        setResult({ results: data });
      }
    } catch (err) {
      console.error("Error fetching scan result:", err);
    }
  }, 5000);

  return () => clearInterval(interval);
}, [scanId]);


  return (
    <div style={{ fontFamily: "Arial", padding: "20px" }}>
      <h1>üõ°Ô∏è IoT Guardian Dashboard</h1>
      <p>Scan your local network for vulnerable IoT devices.</p>

      <div style={{ marginBottom: "10px" }}>
        <label>Network CIDR: </label>
        <input
          type="text"
          value={network}
          onChange={(e) => setNetwork(e.target.value)}
        />
      </div>

      <div style={{ marginBottom: "10px" }}>
        <label>Scan profile: </label>
        <select value={profile} onChange={(e) => setProfile(e.target.value)}>
          <option value="fast">Fast</option>
          <option value="normal">Normal</option>
          <option value="deep">Deep</option>
        </select>
      </div>

      <button onClick={startScan} disabled={loading}>
        {loading ? "Starting..." : "Start Scan"}
      </button>

      {scanId && (
        <p>
          <strong>Scan ID:</strong> {scanId} ‚Äî <em>{status}</em>
        </p>
      )}

      {result && (
        <div>
          <h2>Scan Results</h2>
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>IP</th>
                <th>MAC</th>
                <th>Vendor</th>
                <th>Open Ports</th>
                <th>Risk Score</th>
                <th>Recommendation</th>
              </tr>
            </thead>
            <tbody>
              {result.results.map((r, i) => (
                <tr key={i}>
                  <td>{r.ip}</td>
                  <td>{r.mac}</td>
                  <td>{r.vendor}</td>
                  <td>
                    {Array.isArray(r.open_ports) && r.open_ports.length > 0 ? (
                      r.open_ports.map((p) => (
                        <div key={p.port ?? `${p}`}>{p.port ?? p} {p.service ? `(${p.service})` : ""}</div>
                      ))
                    ) : (
                      <span>‚Äî</span>
                    )}
                  </td>

                  <td>{r.risk?.risk_score}</td>
                  <td>{r.recommendation}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
